func main() {
	// Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ .env Ñ„Ð°Ð¹Ð»
	if err := godotenv.Load(); err != nil {
		// log.Println("No .env file found")
	}

	// Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÐºÐ¾Ð½Ñ„Ð¸Ð³ÑƒÑ€Ð°Ñ†Ð¸ÑŽ
	cfg, err := config.Load()
	if err != nil {
		// log.Fatalf("Failed to load config: %v", err)
	}

	// ÐŸÐ¾Ð´ÐºÐ»ÑŽÑ‡Ð°ÐµÐ¼ÑÑ Ðº Ð‘Ð”
	dbConnection, err := db.Connect(cfg)
	if err != nil {
		// log.Fatalf("Failed to connect to database: %v", err)
	}

	// Ð’Ñ‹Ð¿Ð¾Ð»Ð½ÑÐµÐ¼ Ð¼Ð¸Ð³Ñ€Ð°Ñ†Ð¸Ð¸
	if err := db.Migrate(dbConnection); err != nil {
		// log.Fatalf("Failed to migrate database: %v", err)
	}

	// Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ñ‚ÐµÑÑ‚Ð¾Ð²Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ
	// if err := createTestData(dbConnection); err != nil {
	// log.Printf("Warning: Failed to create test data: %v", err)
	// }

	// Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Gin
	r := gin.Default()

	// ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ° CORS Ð¸Ð· ÐºÐ¾Ð½Ñ„Ð¸Ð³ÑƒÑ€Ð°Ñ†Ð¸Ð¸
	var allowedOrigins []string
	if cfg.CORSAllowedOrigins != "" {
		// ÐŸÐ°Ñ€ÑÐ¸Ð¼ ÑÑ‚Ñ€Ð¾ÐºÑƒ Ñ‡ÐµÑ€ÐµÐ· Ð·Ð°Ð¿ÑÑ‚ÑƒÑŽ
		origins := strings.Split(cfg.CORSAllowedOrigins, ",")
		for _, origin := range origins {
			origin = strings.TrimSpace(origin)
			if origin != "" {
				allowedOrigins = append(allowedOrigins, origin)
			}
		}
	}
	// Fallback Ð½Ð° "*" Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÐµÑÐ»Ð¸ CORSAllowedOrigins Ð¿ÑƒÑÑ‚Ð°Ñ Ð¸Ð»Ð¸ Ð½Ðµ ÑÐ¾Ð´ÐµÑ€Ð¶Ð¸Ñ‚ Ð²Ð°Ð»Ð¸Ð´Ð½Ñ‹Ñ… Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ð¹
	if len(allowedOrigins) == 0 {
		allowedOrigins = []string{"*"}
	}

	r.Use(cors.New(cors.Config{
		AllowOrigins:     allowedOrigins,
		AllowMethods:     []string{"GET", "POST", "PUT", "DELETE", "OPTIONS"},
		AllowHeaders:     []string{"Origin", "Content-Type", "Authorization", "Accept"},
		AllowCredentials: true,
		MaxAge:           12 * time.Hour,
	}))

	// ÐÐ°ÑÑ‚Ñ€Ð°Ð¸Ð²Ð°ÐµÐ¼ Ð¼Ð°Ñ€ÑˆÑ€ÑƒÑ‚Ñ‹
	routes.SetupAuthRoutes(r, dbConnection, cfg)
	routes.SetupPlaceRoutes(r, dbConnection, cfg)
	routes.SetupRouteRoutes(r, dbConnection)
	routes.SetupReviewRoutes(r, dbConnection, cfg)
	routes.SetupFavoriteRoutes(r, dbConnection, cfg)
	routes.SetupActivityRoutes(r, dbConnection, cfg)

	// Ð¢ÐµÑÑ‚Ð¾Ð²Ñ‹Ð¹ ÑÐ½Ð´Ð¿Ð¾Ð¸Ð½Ñ‚
	r.GET("/ping", func(c *gin.Context) {
		c.JSON(200, gin.H{
			"message": "pong",
		})
	})

	// Ð—Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ ÑÐµÑ€Ð²ÐµÑ€
	// log.Printf("âœ… Server starting on port %s", cfg.Port)
	// log.Printf("ðŸ“ Ð”Ð¾ÑÑ‚ÑƒÐ¿Ð½Ñ‹Ðµ ÑÐ½Ð´Ð¿Ð¾Ð¸Ð½Ñ‚Ñ‹:")
	// log.Printf("   POST   /auth/register")
	// log.Printf("   POST   /auth/login")
	// log.Printf("   POST   /auth/forgot-password")
	// log.Printf("   POST   /auth/verify-reset-code")
	// log.Printf("   POST   /auth/reset-password")
	// log.Printf("   DELETE /auth/delete-account (protected)")
	// log.Printf("   POST   /auth/upload-avatar (protected)") // Ð”ÐžÐ‘ÐÐ’Ð›Ð•ÐÐž
	// log.Printf("   DELETE /auth/delete-avatar (protected)") // Ð”ÐžÐ‘ÐÐ’Ð›Ð•ÐÐž
	// log.Printf("   GET    /ping")
	// log.Printf("   GET    /places")
	// log.Printf("   GET    /routes")
	// log.Printf("   GET    /reviews/place/:placeId")
	// log.Printf("   POST   /reviews (protected)")
	// log.Printf("   GET    /favorites/places (protected)")
	// log.Printf("   POST   /favorites/places/:placeId (protected)")
	// log.Printf("   DELETE /favorites/places/:placeId (protected)")
	// log.Printf("   GET    /favorites/places/:placeId/status (protected)")
	// log.Printf("   GET    /user/activity (protected)")
	// log.Printf("   GET    /user/activity/places (protected)")
	// log.Printf("   GET    /user/activity/routes (protected)")
	// log.Printf("   POST   /user/activity/places/:placeId (protected)")
	// log.Printf("   POST   /user/activity/routes/:routeId (protected)")
	// log.Printf("   DELETE /user/activity/places/:placeId (protected)")
	// log.Printf("   DELETE /user/activity/routes/:routeId (protected)")

	if err := r.Run(":" + cfg.Port); err != nil {
		// log.Fatalf("Failed to run server: %v", err)
	}
}

